import React, { useState } from "react";
import axios from "./utils/axios";

const Sample = () => {
  const [sample, setSample] = useState("");
  const [sampleList, setsampleList] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    setSample(e.target.value);
  };

  const getSample = () => {
    setLoading(true);
    axios
      .post("/getSample", { sample: sample })
      .then((res) => {
        console.log("it worked")
        console.log(res.data)
        setsampleList(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.log("it failed")
        console.error(err);
        setLoading(false);
      });
  };

  return (
    <div>
      <div display="flex" justifyContent="center">
      <h1>Find Samples!</h1>
      <input type="text" value={sample} onChange={handleInputChange} />
      </div>
      <button onClick={getSample}>Get Sample</button>
      <button onClick={getHome}>Get Home</button>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <ul>
          {sampleList.map((track) => (
            <li key={track.track.id}>
              {track.track.name} by {track.track.artists[0].name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Sample;
